export const getSavedPropertyIds = () => {
    const savedPropertyIds = localStorage.getItem('saved_properties')
      ? JSON.parse(localStorage.getItem('saved_properties'))
      : [];
  
    return savedPropertyIds;
  };
  
  export const savePropertyIds = (listing_idArr) => {
    if (listing_idArr.length) {
      localStorage.setItem('saved_properties', JSON.stringify(listing_idArr));
    } else {
      localStorage.removeItem('saved_properties');
    }
  };
  
  export const removePropertyId = (listing_id) => {
    const savedPropertyIds = localStorage.getItem('saved_properties')
      ? JSON.parse(localStorage.getItem('saved_properties'))
      : null;
  
    if (!savedPropertyIds) {
      return false;
    }
  
    const updatedSavedPropertyIds = savedPropertyIds?.filter((savedPropertyId) => savedPropertyId !== listing_id);
    localStorage.setItem('saved_properties', JSON.stringify(updatedSavedPropertyIds));
  
    return true;
  };
